CREATE OR REPLACE FUNCTION telematic_json_pck.obter_valor_split ( ds_valor_p text, ds_place_p text DEFAULT 'F', ds_delimiter_p text DEFAULT ' ') RETURNS varchar AS $body$
DECLARE


ds_delimiter_idx 	bigint := 0;


BEGIN

ds_delimiter_idx := INSTR(ds_valor_p, ds_delimiter_p, -1);

IF (UPPER(ds_place_p) = 'F') THEN
	RETURN SUBSTR(ds_valor_p, 0, ds_delimiter_idx - LENGTH(ds_delimiter_p));
ELSIF (UPPER(ds_place_p) = 'L') THEN
	RETURN SUBSTR(ds_valor_p, ds_delimiter_idx + LENGTH(ds_delimiter_p));
END IF;

RETURN ds_valor_p;

END;

