CREATE OR REPLACE FUNCTION timezone_utils.converttimezone (dateTime timestamp, sourceTimeZone text, targetTimeZone text) RETURNS timestamp AS $body$
DECLARE

        dst_exception exception;

    
BEGIN
        if (not timezone_utils.hasbaselinetimezone()) then
            return dateTime;
        end if;

        if (coalesce(sourceTimeZone::text, '') = '') then
            RAISE EXCEPTION '%', 'sourceTimeZone cannot be null' USING ERRCODE = '45011';
        end if;
        if (coalesce(targetTimeZone::text, '') = '') then
            RAISE EXCEPTION '%', 'targetTimeZone cannot be null' USING ERRCODE = '45011';
        end if;

        begin
            return (dateTime, sourceTimeZone)::timestamp with time zone at time zone targetTimeZone;
        exception
            when dst_exception then
                return timezone_utils.converttimezone(dateTime - interval '1' hour, sourceTimeZone, targetTimeZone) + interval '1' hour;
        end;
    end;


