SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION tiss_convenio_pck.get_tiss_param_convenio ( cd_convenio_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

indice_w		bigint;	
ie_data_despesa_w	varchar(40);
BEGIN


for i in 1..tiss_param_conv_vet_w.count loop
	begin
	
	if (current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet[i].cd_convenio		= cd_convenio_p) and (current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet[i].cd_estabelecimento	= cd_estabelecimento_p) then
		
		if (ie_opcao_p	=	'DD') then
			return current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet[i].IE_DATA_DESPESA;
		end if;
		
	end if;
	
	end;
end loop;




select	coalesce(max(IE_DATA_DESPESA), 'C')
into STRICT	ie_data_despesa_w
from	tiss_parametros_convenio
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p;


indice_w	:= current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet.count+1;

current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet[indice_w].cd_convenio		:= cd_convenio_p;
current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet[indice_w].cd_estabelecimento	:= cd_estabelecimento_p;
current_setting('tiss_convenio_pck.tiss_param_conv_vet_w')::tiss_param_conv_vet[indice_w].IE_DATA_DESPESA		:= ie_data_despesa_w;


if (ie_opcao_p	=	'DD') then
	return ie_data_despesa_w;
end if;


return null;

END;

$body$
