CREATE OR REPLACE FUNCTION tiss_dados_xml_pck.obter_lote_guia (nr_seq_guia_p tiss_dem_conta.nr_seq_lote%type) RETURNS bigint AS $body$
DECLARE

	nr_lote_w	tiss_dem_lote.nr_lote%type;
	

BEGIN

	begin
		select	b.nr_lote
		into STRICT	nr_lote_w
		from	tiss_dem_lote b
