CREATE OR REPLACE FUNCTION tiss_dados_xml_pck.obter_retorno_guia (nr_seq_guia_p tiss_dem_conta.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


	nr_seq_retorno_w	convenio_retorno.nr_sequencia%type;


BEGIN

	begin
		select	e.nr_seq_retorno
		into STRICT	nr_seq_retorno_w	
		from	tiss_cabecalho e,
			tiss_demonstrativo d,
			tiss_dem_fatura c,
			tiss_dem_lote b,
			tiss_dem_conta a
		where	e.nr_sequencia = d.nr_seq_cabecalho
		and	d.nr_sequencia = c.nr_seq_demonstrativo
		and	c.nr_sequencia = b.nr_seq_fatura
		and	b.nr_sequencia = a.nr_seq_lote
		and	a.nr_sequencia = nr_seq_guia_p;		
	exception
	when others then
		nr_seq_retorno_w	:= 0;
	end;


	return nr_seq_retorno_w;
END;

$body$
