CREATE OR REPLACE FUNCTION tiss_dashboard_color_pck.get_color_min (vl_item_p bigint, vl_tgt_med_p bigint, vl_tgt_max_p bigint) RETURNS varchar AS $body$
DECLARE


ds_color_w varchar(200);

vl_tgt_med_w	bigint := coalesce(vl_tgt_med_p, 5);
vl_tgt_max_w	bigint := coalesce(vl_tgt_max_p, 10);


BEGIN
	
	if 	vl_item_p > vl_tgt_max_w then
		ds_color_w := current_setting('tiss_dashboard_color_pck.second_column')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_red')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_separator')::varchar(1) || current_setting('tiss_dashboard_color_pck.fourth_column')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_red')::varchar(20);
	elsif	vl_item_p > vl_tgt_med_w then
		ds_color_w := current_setting('tiss_dashboard_color_pck.second_column')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_yellow')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_separator')::varchar(1) || current_setting('tiss_dashboard_color_pck.fourth_column')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_yellow')::varchar(20);
	else
		ds_color_w := current_setting('tiss_dashboard_color_pck.second_column')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_green')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_separator')::varchar(1) || current_setting('tiss_dashboard_color_pck.fourth_column')::varchar(20) || current_setting('tiss_dashboard_color_pck.color_green')::varchar(20);
	end if;

	return ds_color_w;

end;


