SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE tiss_dashboard_data_pck.calcula_item_datas (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type, vl_item_prox_venc_p INOUT bigint, qt_item_prox_venc_p INOUT bigint, vl_item_vencido_p INOUT bigint, qt_item_vencido_p INOUT bigint, vl_item_sem_resp_p INOUT bigint, qt_item_sem_resp_p INOUT bigint) AS $body$
DECLARE

	
qt_item_w	bigint;
vl_item_w	bigint;

dt_atual_trc_w		timestamp;
dt_ultimo_ret_guia_w	timestamp;
qt_dias_reap_conta_w	bigint;
qt_dias_glosa_conta_w	bigint;

dt_fechamento_lote_w timestamp;
dt_fechamento_analise_w timestamp;
dt_envio_analise_w timestamp;
dt_baixa_glosa_w timestamp;
	

BEGIN

	vl_item_prox_venc_p := 0;
	qt_item_prox_venc_p := 0;
	vl_item_vencido_p := 0;
	qt_item_vencido_p := 0;
	vl_item_sem_resp_p := 0;
	qt_item_sem_resp_p := 0;

	select 	trunc(clock_timestamp(), 'dd'),
		tiss_dashboard_data_pck.obter_dt_ultimo_retorno(nr_interno_conta_p, cd_autorizacao_p),
		tiss_dashboard_data_pck.obter_dias_reap_conta(nr_interno_conta_p),
		tiss_dashboard_data_pck.obter_dias_rec_glosa_conta(nr_interno_conta_p)
	into STRICT	dt_atual_trc_w,
		dt_ultimo_ret_guia_w,
		qt_dias_reap_conta_w,
		qt_dias_glosa_conta_w
	;
	
	select	count(a.nr_sequencia) qt,
		coalesce(sum(a.vl_amenor),0) vl_glosa,
		max(d.dt_fechamento) dt_fech_lote,
		max(c.dt_fechamento) dt_fech_analise,
		max(c.dt_envio) dt_envio,
		max(c.dt_baixa_glosa) dt_baixa
	into STRICT	qt_item_w,
		vl_item_w,
		dt_fechamento_lote_w,
		dt_fechamento_analise_w,
		dt_envio_analise_w,
		dt_baixa_glosa_w
	from	lote_audit_hist_item a,
		lote_audit_hist_guia b,
		lote_audit_hist c,
		lote_auditoria d
	where	a.nr_seq_guia 		= b.nr_sequencia
	and	b.nr_seq_lote_hist 	= c.nr_sequencia
	and	c.nr_seq_lote_audit 	= d.nr_sequencia
	and	b.nr_interno_conta 	= nr_interno_conta_p	
	and	c.nr_analise = (SELECT max(x.nr_analise) from lote_audit_hist x where x.nr_seq_lote_audit = d.nr_sequencia)
	and	coalesce(b.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738)) 	= cd_autorizacao_p
	and	a.ie_acao_glosa 	= 'R';
	
	if 	coalesce(dt_fechamento_lote_w::text, '') = ''
		and	coalesce(dt_fechamento_analise_w::text, '') = ''
		and	coalesce(dt_envio_analise_w::text, '') = ''
		and 	((dt_ultimo_ret_guia_w + qt_dias_reap_conta_w) - dt_atual_trc_w)  between 0 and 15 then
		
		vl_item_prox_venc_p := vl_item_w;
		qt_item_prox_venc_p := qt_item_w;
		
	elsif 	coalesce(dt_fechamento_lote_w::text, '') = ''
		and	coalesce(dt_fechamento_analise_w::text, '') = ''
		and	coalesce(dt_envio_analise_w::text, '') = ''
		and 	dt_atual_trc_w > (dt_ultimo_ret_guia_w + qt_dias_reap_conta_w) then
		
		vl_item_vencido_p := vl_item_w;
		qt_item_vencido_p := qt_item_w;	
		
	elsif 	(dt_fechamento_analise_w IS NOT NULL AND dt_fechamento_analise_w::text <> '')
		and	(dt_envio_analise_w IS NOT NULL AND dt_envio_analise_w::text <> '')
		and 	dt_atual_trc_w > (dt_envio_analise_w + qt_dias_glosa_conta_w)
		and 	tiss_dashboard_data_pck.obter_se_guia_paga_pos_data(nr_interno_conta_p, cd_autorizacao_p, dt_envio_analise_w) = 'N' then
		
		vl_item_sem_resp_p := vl_item_w;
		qt_item_sem_resp_p := qt_item_w;
		
	end if;	

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE tiss_dashboard_data_pck.calcula_item_datas (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type, vl_item_prox_venc_p INOUT bigint, qt_item_prox_venc_p INOUT bigint, vl_item_vencido_p INOUT bigint, qt_item_vencido_p INOUT bigint, vl_item_sem_resp_p INOUT bigint, qt_item_sem_resp_p INOUT bigint) FROM PUBLIC;
