CREATE OR REPLACE PROCEDURE tiss_dashboard_data_pck.calcula_item_prox_venc (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type, vl_item_p INOUT bigint, qt_item_p INOUT bigint) AS $body$
DECLARE


qt_item_vencido_w bigint;
vl_item_vencido_w double precision;

dt_atual_trc_w		timestamp;
dt_ultimo_ret_guia_w	timestamp;
qt_dias_glosa_conta_w	bigint;


BEGIN

	begin
		select 	trunc(clock_timestamp(), 'dd'),
			tiss_dashboard_data_pck.obter_dt_ultimo_retorno(nr_interno_conta_p, cd_autorizacao_p),
			tiss_dashboard_data_pck.obter_dias_reap_conta(nr_interno_conta_p)
		into STRICT	dt_atual_trc_w,
			dt_ultimo_ret_guia_w,
			qt_dias_glosa_conta_w
		;
	exception
	when 	others then
		dt_atual_trc_w := null;
		dt_ultimo_ret_guia_w := null;
		qt_dias_glosa_conta_w :=  null;
	end;
	
	if ((dt_atual_trc_w IS NOT NULL AND dt_atual_trc_w::text <> '') and (dt_ultimo_ret_guia_w IS NOT NULL AND dt_ultimo_ret_guia_w::text <> '') and (qt_dias_glosa_conta_w IS NOT NULL AND qt_dias_glosa_conta_w::text <> '')) then
		select	count(a.nr_sequencia) qt,
			coalesce(sum(a.vl_amenor),0) vl_glosa
		into STRICT	qt_item_vencido_w,
			vl_item_vencido_w
		from	lote_audit_hist_item a,
			lote_audit_hist_guia b,
			lote_audit_hist c,
			lote_auditoria d
		where	a.nr_seq_guia 		= b.nr_sequencia
		and	b.nr_seq_lote_hist 	= c.nr_sequencia
		and	c.nr_seq_lote_audit 	= d.nr_sequencia
		and	b.nr_interno_conta 	= nr_interno_conta_p
		and	coalesce(d.dt_fechamento::text, '') = ''
		and	coalesce(c.dt_fechamento::text, '') = ''
		and	coalesce(c.dt_envio::text, '') = ''
		and	coalesce(b.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738)) 	= cd_autorizacao_p
		and	a.ie_acao_glosa 	= 'R'
		and	(dt_atual_trc_w - (dt_ultimo_ret_guia_w + qt_dias_glosa_conta_w) <= 15
			and (dt_ultimo_ret_guia_w + qt_dias_glosa_conta_w) <= dt_atual_trc_w)
			;
	end if;
	vl_item_p := coalesce(vl_item_vencido_w, 0);
	qt_item_p := coalesce(qt_item_vencido_w, 0);

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
