SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE tiss_dashboard_data_pck.calcula_item_vencido (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type, vl_item_p INOUT bigint, qt_item_p INOUT bigint) AS $body$
DECLARE


qt_item_vencido_w bigint;
vl_item_vencido_w double precision;


BEGIN
	select	count(a.nr_sequencia) qt,
		coalesce(sum(a.vl_amenor),0) vl_glosa
	into STRICT	qt_item_vencido_w,
		vl_item_vencido_w
	from	lote_audit_hist_item a,
		lote_audit_hist_guia b,
		lote_audit_hist c,
		lote_auditoria d
	where	a.nr_seq_guia 		= b.nr_sequencia
	and	b.nr_seq_lote_hist 	= c.nr_sequencia
	and	c.nr_seq_lote_audit 	= d.nr_sequencia	
	and	coalesce(d.dt_fechamento::text, '') = ''
	and	coalesce(c.dt_fechamento::text, '') = ''
	and	coalesce(c.dt_envio::text, '') = ''
	and	c.nr_analise = (SELECT max(x.nr_analise) from lote_audit_hist x where x.nr_seq_lote_audit = d.nr_sequencia)
	and	b.nr_interno_conta 	= nr_interno_conta_p
	and	coalesce(b.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738))	= cd_autorizacao_p
	and	a.ie_acao_glosa 	= 'R'	
	and	trunc(clock_timestamp(), 'dd')	> (tiss_dashboard_data_pck.obter_dt_ultimo_retorno(b.nr_interno_conta, b.cd_autorizacao) + tiss_dashboard_data_pck.obter_dias_reap_conta(b.nr_interno_conta));


	vl_item_p := coalesce(vl_item_vencido_w, 0);
	qt_item_p := coalesce(qt_item_vencido_w, 0);

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
