CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_dias_reap_conta (nr_interno_conta_p conta_paciente.nr_interno_conta%type) RETURNS CONVENIO_ESTABELECIMENTO.QT_DIAS_REAPRE%TYPE AS $body$
DECLARE


qt_dias_w bigint;


BEGIN
	begin
		select 	x.qt_dias_reapre
		into STRICT	qt_dias_w
		from	convenio_estabelecimento x
		where 	x.cd_convenio = (Obter_cd_ds_convenio_conta(nr_interno_conta_p, 'CD'))::numeric
		and 	x.cd_estabelecimento = tiss_dashboard_data_pck.obter_estabelecimento_conta(nr_interno_conta_p);
	exception
	when others then
		qt_dias_w := null;
	end;

	if coalesce(qt_dias_w::text, '') = '' then
		select	coalesce(x.qt_dias_reapresentacao, 30)
		into STRICT	qt_dias_w
		from	convenio x
		where	x.cd_convenio = (Obter_cd_ds_convenio_conta(nr_interno_conta_p, 'CD'))::numeric;
	end if;

	return qt_dias_w;

end;


$body$
