CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_dt_envio_recurso (nr_interno_conta_p lote_audit_hist_guia.nr_interno_conta%type, cd_autorizacao_p lote_audit_hist_guia.cd_autorizacao%type) RETURNS timestamp AS $body$
DECLARE


dt_recurso_w	lote_audit_hist.dt_envio%type;


BEGIN

	begin
		select	min(b.dt_envio)
		into STRICT	dt_recurso_w
		from	lote_audit_hist_guia a,
			lote_audit_hist b
		where 	a.nr_seq_lote_hist 	= b.nr_sequencia
		and	coalesce(a.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738)) 	= cd_autorizacao_p
		and	a.nr_interno_conta 	= nr_interno_conta_p;
	exception
	when others then
		dt_recurso_w := null;
	end;

	return dt_recurso_w;

end;


$body$
