CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_max_cd_tiss_conv (cd_convenio_p convenio.cd_convenio%type, dt_ref_p timestamp) RETURNS TISS_MOTIVO_GLOSA.CD_MOTIVO_TISS%TYPE AS $body$
DECLARE


cd_mot_w tiss_motivo_glosa.cd_motivo_tiss%type;


BEGIN

	select	cd
	into STRICT	cd_mot_w
	from (
		SELECT  a.cd_motivo_ans cd	
		from 	motivo_glosa_itens a,
			conciliacao_conta_pac_guia b,
			conciliacao_conta_paciente c,
			protocolo_faturado d
		where 	a.nr_seq_conciliacao_guia = b.nr_sequencia
		and 	b.nr_seq_conta_pac_conc = c.nr_sequencia
		and 	c.nr_seq_protoc_faturado = d.nr_sequencia
		and (coalesce(dt_ref_p::text, '') = '' or trunc(d.dt_referencia, 'mm') = trunc(dt_ref_p, 'mm'))
		and 	d.cd_convenio = cd_convenio_p
		and 	(a.cd_motivo_ans IS NOT NULL AND a.cd_motivo_ans::text <> '')
		group by a.cd_motivo_ans
		order by sum(a.qt_incidencia) desc
	) alias6 LIMIT 1;

	return cd_mot_w;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
