CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_nota_fiscal (nr_seq_protocolo_p protocolo_convenio.nr_seq_protocolo%type) RETURNS varchar AS $body$
DECLARE


ds_nota_fiscal_w 	varchar(255);

nota_fiscal CURSOR FOR
SELECT	nr_nota_fiscal
from	nota_fiscal
where	nr_seq_protocolo = nr_seq_protocolo_p;

BEGIN
	ds_nota_fiscal_w := null;
	begin
		for	nota_r in nota_fiscal loop
			if coalesce(ds_nota_fiscal_w::text, '') = '' then
				ds_nota_fiscal_w := nota_r.nr_nota_fiscal;
			else
				ds_nota_fiscal_w := ds_nota_fiscal_w || ', ' || nota_r.nr_nota_fiscal;
			end if;
		end loop;
	exception
	when others then
		ds_nota_fiscal_w := null;
	end;

	return substr(ds_nota_fiscal_w, 1, 255);

end;



