CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_qt_item_conv (cd_convenio_p convenio.cd_convenio%type, dt_ref_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_item_w bigint;


BEGIN

	begin
		select	count(a.nr_sequencia)
		into STRICT	qt_item_w
		from	motivo_glosa_itens a,
			conciliacao_conta_pac_guia b,
			conciliacao_conta_paciente c,
			protocolo_faturado d
		where	a.nr_seq_conciliacao_guia       = b.nr_sequencia
		and	b.nr_seq_conta_pac_conc		= c.nr_sequencia
		and	c.nr_seq_protoc_faturado	= d.nr_sequencia
		and	d.cd_convenio			= cd_convenio_p
		and (coalesce(dt_ref_p::text, '') = '' or trunc(d.dt_referencia, 'mm') = trunc(dt_ref_p, 'mm'));
	exception
	when others then
		qt_item_w := 0;
	end;

	return qt_item_w;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
