SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_saldo_titulo_conta (nr_interno_conta_p conta_paciente_guia.nr_interno_conta%type) RETURNS bigint AS $body$
DECLARE


vl_saldo_w	double precision := 0;	

titulo CURSOR FOR
SELECT	vl_saldo_titulo
from	titulo_receber
where	nr_interno_conta = nr_interno_conta_p;

BEGIN

	begin
		for tit_r in titulo loop
			vl_saldo_w := vl_saldo_w + tit_r.vl_saldo_titulo;
		end loop;
	exception
	when others then
		vl_saldo_w := 0;
	end;

	return coalesce(vl_saldo_w, 0);

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
