CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_se_guia_paga_pos_data (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type, dt_base_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_paga_w	varchar(1);


BEGIN
	begin
		select	coalesce(max('S'), 'N')
		into STRICT	ie_paga_w
		from	convenio_retorno a,
			convenio_retorno_item b
		where	b.nr_seq_retorno = a.nr_sequencia
		and	b.nr_interno_conta = nr_interno_conta_p
		and	coalesce(b.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738)) = cd_autorizacao_p
		and	a.dt_retorno > dt_base_p;
	exception
	when others then
		ie_paga_w := 'N';
	end;

	return coalesce(ie_paga_w, 'N');
end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
