CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_se_prot_possui_guia_inad (nr_seq_protocolo_p protocolo_convenio.nr_seq_protocolo%type) RETURNS varchar AS $body$
DECLARE


ie_possui_w varchar(10) := 'N';

guias CURSOR FOR
SELECT	a.cd_autorizacao	cd_autorizacao,
	a.nr_interno_conta	nr_interno_conta
from	conta_paciente_guia a,
	conta_paciente b
where	a.nr_interno_conta = b.nr_interno_conta
and	b.nr_seq_protocolo = nr_seq_protocolo_p;

BEGIN

	RAISE NOTICE 'nr_seq_protocolo_p = %', nr_seq_protocolo_p;
	
	for guia in guias loop
		RAISE NOTICE 'guia.cd_autorizacao = %', guia.cd_autorizacao;
		RAISE NOTICE 'guia.nr_interno_conta = %', guia.nr_interno_conta;

		ie_possui_w := tiss_dashboard_data_pck.obter_se_guia_inadimplente(guia.nr_interno_conta, guia.cd_autorizacao, tiss_dashboard_data_pck.obter_pagamento_previsto(guia.nr_interno_conta, coalesce(guia.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738))));
		
		if ie_possui_w = 'S' then
			exit;
		end if;
	
	end loop;
	
	return ie_possui_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
