CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_se_saldo_difere_guia (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type) RETURNS varchar AS $body$
DECLARE


ie_difere_w varchar(1);

vl_saldo_w double precision;

qt_w bigint;


BEGIN

	begin
		select 	coalesce(count(*), 0) qt
		into STRICT	qt_w
		from	conciliacao_conta_pac_guia
		where	nr_interno_conta = nr_interno_conta_p
		and	cd_autorizacao = cd_autorizacao_p;
	exception
	when others then
		qt_w := 0;
	end;

	if qt_w = 0 then
		ie_difere_w := 'S';	
	else
		select 	vl_saldo_titulo
		into STRICT	vl_saldo_w
		from	conciliacao_conta_pac_guia
		where	nr_interno_conta = nr_interno_conta_p
		and	cd_autorizacao = cd_autorizacao_p;

		if vl_saldo_w <> tiss_dashboard_data_pck.obter_saldo_titulo_guia(nr_interno_conta_p, cd_autorizacao_p) then
			ie_difere_w := 'S';
		else
			ie_difere_w := 'N';
		end if;

	end if;

	return coalesce(ie_difere_w, 'S');
end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
