CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_se_saldo_difere_prot (nr_seq_protocolo_p protocolo_faturado.nr_seq_protocolo%type) RETURNS varchar AS $body$
DECLARE


ie_difere_w varchar(1);

vl_saldo_w double precision;

qt_w bigint;
dt_envio_recurso_w  timestamp;
dt_envio_grg_w  timestamp;
cd_convenio_w   convenio.cd_convenio%type;
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type;


BEGIN
	ie_difere_w := 'N';
	begin
		select 	coalesce(count(*), 0) qt
		into STRICT	qt_w
		from	protocolo_faturado
		where	nr_seq_protocolo = nr_seq_protocolo_p;
	exception
	when others then
		qt_w := 0;
	end;

	if qt_w = 0 then
		ie_difere_w := 'S';
	else
		select 	vl_saldo_titulo, dt_envio_recurso, cd_convenio, cd_estabelecimento
		into STRICT	vl_saldo_w,
                dt_envio_recurso_w,
                cd_convenio_w,
                cd_estabelecimento_w
		from	protocolo_faturado
		where	nr_seq_protocolo = nr_seq_protocolo_p;

		if vl_saldo_w <> tiss_dashboard_data_pck.obter_saldo_titulo_prot(nr_seq_protocolo_p) then
			ie_difere_w := 'S';
		end if;
	end if;

	return coalesce(ie_difere_w, 'N');
end;




