CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_valor_inad_guia (nr_seq_conta_pac_guia_p conciliacao_conta_pac_guia.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


vl_inadimplencia_w double precision := 0;


BEGIN

	begin
		select	coalesce(sum(coalesce(a.vl_guia,0)), 0)
		into STRICT	vl_inadimplencia_w
		from	conciliacao_conta_pac_guia a
		where	coalesce(a.ie_inadimplencia, 'N') = 'S'
		and	a.nr_sequencia = nr_seq_conta_pac_guia_p;
	exception
	when others then
		vl_inadimplencia_w := 0;
	end;
	
	return vl_inadimplencia_w;

end;

$body$
