CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_valor_inadimplencia (cd_convenio_p convenio.cd_convenio%type, dt_ref_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_inadimplencia_w double precision := 0;


BEGIN

	begin
		select	coalesce(sum(coalesce(a.vl_guia,0)), 0)
		into STRICT	vl_inadimplencia_w
		from	conciliacao_conta_pac_guia a,
			conciliacao_conta_paciente b,
			protocolo_faturado c
		where	coalesce(a.ie_inadimplencia, 'N') = 'S'
		and	a.nr_seq_conta_pac_conc = b.nr_sequencia
		and	b.nr_seq_protoc_faturado = c.nr_sequencia
		and	c.cd_convenio	= cd_convenio_p
		and	trunc(c.dt_referencia, 'mm') = trunc(coalesce(dt_ref_p, add_months(clock_timestamp(), -2)), 'mm');
	exception
	when others then
		vl_inadimplencia_w := 0;
	end;
	
	return vl_inadimplencia_w;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
