SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_valores_guia (nr_interno_conta_p conta_paciente_guia.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_func_w double precision;

vl_adic_w 	double precision;
vl_desc_w 	double precision;
vl_glosa_w 	double precision;
vl_recursado_w	double precision;


BEGIN
	select	sum(coalesce(a.vl_adicional,0)),
		sum(coalesce(a.vl_desconto,0)),
		sum(coalesce(a.vl_glosado,0)),
		sum(coalesce(a.vl_amenor,0))
	into STRICT	vl_adic_w,
		vl_desc_w,
		vl_glosa_w,
		vl_recursado_w
	from	convenio_retorno_item a,
		convenio_retorno b
	where	a.nr_seq_retorno = b.nr_sequencia
	and	b.ie_status_retorno = 'F'
	and	a.nr_interno_conta = nr_interno_conta_p
	and	coalesce(a.cd_autorizacao, wheb_mensagem_pck.get_texto(1097738)) = cd_autorizacao_p;


	select	vl_glosa_w + coalesce(sum(a.vl_glosa),0) vl_glosa
	into STRICT	vl_glosa_w
	from	lote_audit_hist c,
		lote_audit_hist_item a,
		lote_audit_hist_guia b
	where	b.nr_sequencia			= a.nr_seq_guia
	and	coalesce(b.cd_autorizacao,'0')	= coalesce(coalesce(cd_autorizacao_p,b.cd_autorizacao),'0')
	and	b.nr_interno_conta		= nr_interno_conta_p
	and	b.nr_seq_lote_hist		= c.nr_sequencia
	and ((b.dt_baixa_glosa IS NOT NULL AND b.dt_baixa_glosa::text <> '') or c.nr_sequencia <> (obter_ultima_analise(c.nr_seq_lote_audit))::numeric );	

	if ie_tipo_p = 'ADIC' then
		vl_retorno_func_w := vl_adic_w;
	elsif ie_tipo_p = 'DESC' then
		vl_retorno_func_w := vl_desc_w;
	elsif ie_tipo_p = 'GLOSA' then
		vl_retorno_func_w := vl_glosa_w;
	elsif ie_tipo_p = 'RECEB' then
		vl_retorno_func_w := tiss_dashboard_data_pck.obter_valor_recebido_guia(nr_interno_conta_p, cd_autorizacao_p);
	elsif ie_tipo_p = 'REC' then -- recursado
		vl_retorno_func_w := vl_recursado_w;
	else
		vl_retorno_func_w := tiss_dashboard_data_pck.obter_valor_recuperado_guia(nr_interno_conta_p, cd_autorizacao_p);
	end if;

	return vl_retorno_func_w;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
