CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_vencimento_guia (nr_interno_conta_p conta_paciente.nr_interno_conta%type, cd_autorizacao_p conta_paciente_guia.cd_autorizacao%type) RETURNS timestamp AS $body$
DECLARE


dt_vencimento_w timestamp;
nr_seq_protocolo_w protocolo_convenio.nr_seq_protocolo%type;


BEGIN

	begin
		select	a.dt_vencimento
		into STRICT	dt_vencimento_w
		from	titulo_receber a
		where	a.nr_titulo = OBTER_TITULO_CONTA_GUIA(nr_interno_conta_p, cd_autorizacao_p, null, null);
	exception
	when others then
		dt_vencimento_w := null;
	end;

	if coalesce(dt_vencimento_w::text, '') = '' then

		begin
			select	a.dt_vencimento
			into STRICT	dt_vencimento_w
			from	titulo_receber a
			where	a.nr_titulo = (								
				SELECT 	max(z.nr_titulo)
				from	titulo_receber z
				where	z.nr_interno_conta = nr_interno_conta_p
			);
		exception
		when others then
			dt_vencimento_w := null;
		end;

		if coalesce(dt_vencimento_w::text, '') = '' then

			nr_seq_protocolo_w := tiss_dashboard_data_pck.obter_protocolo_conta(nr_interno_conta_p);

			begin
				select	a.dt_vencimento
				into STRICT	dt_vencimento_w
				from	titulo_receber a
				where	a.nr_titulo = (
							SELECT max(z.nr_titulo)
							from titulo_receber z
							where z.nr_seq_protocolo = nr_seq_protocolo_w
				);
			exception
			when others then
				dt_vencimento_w	:= null;
			end;

		end if;

	end if;

	return dt_vencimento_w;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
