CREATE OR REPLACE FUNCTION tiss_dashboard_data_pck.obter_vencimento_protocolo (nr_seq_protocolo_p protocolo_convenio.nr_seq_protocolo%type) RETURNS timestamp AS $body$
DECLARE


dt_vencimento_w		timestamp;


BEGIN

	begin
		select	a.dt_vencimento
		into STRICT	dt_vencimento_w
		from	protocolo_convenio a
		where	a.nr_seq_protocolo = nr_seq_protocolo_p;
	exception
	when others then
		dt_vencimento_w := null;
	end;

	if coalesce(dt_vencimento_w::text, '') = '' then

		begin
			select	a.dt_vencimento
			into STRICT	dt_vencimento_w
			from	titulo_receber a
			where	a.nr_titulo = (
					SELECT max(z.nr_titulo)
					from titulo_receber z
					where z.nr_seq_protocolo = nr_seq_protocolo_p
			);
		exception
		when others then
			dt_vencimento_w	:= null;
		end;

		if coalesce(dt_vencimento_w::text, '') = '' then
			begin
				select	a.dt_vencimento
				into STRICT	dt_vencimento_w
				from	titulo_receber a
				where	a.nr_titulo = (								
					SELECT 	max(z.nr_titulo)
					from	titulo_receber z
					where	z.nr_interno_conta in (
						SELECT y.nr_interno_conta
						from conta_paciente y
						where y.nr_seq_protocolo = nr_seq_protocolo_p
					)
				);
			exception
			when others then
				dt_vencimento_w := null;
			end;

			if coalesce(dt_vencimento_w::text, '') = '' then
				begin
					select	a.dt_vencimento
					into STRICT	dt_vencimento_w
					from	titulo_receber a
					where	a.nr_titulo in (
						SELECT	OBTER_TITULO_CONTA_GUIA(z.nr_interno_conta, z.cd_autorizacao, null, null)
						from	conta_paciente_guia z
						where	z.nr_interno_conta in (
							SELECT y.nr_interno_conta
							from	conta_paciente y
							where	y.nr_seq_protocolo = nr_seq_protocolo_p
						)					
					);
				exception
				when others then
					dt_vencimento_w := null;
				end;
			end if;

		end if;

	end if;

	return dt_vencimento_w;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
