SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION tiss_ins_ret_denial_pck.get_item_balance ( nr_seq_item_fatur_p bigint, ie_proc_desp_p text ) RETURNS bigint AS $body$
DECLARE

		
result_w	double precision;
vl_item_w	double precision;


BEGIN

	result_w := 0;
	vl_item_w := 0;
	
	if ie_proc_desp_p = 'P' then
	
		select	x.vl_procedimento
		into STRICT	vl_item_w
		from	tiss_conta_proc x
		where	x.nr_sequencia = nr_seq_item_fatur_p;
		
		result_w := tiss_ins_ret_denial_pck.get_item_paid_value(nr_seq_item_fatur_p, null);
		
	elsif ie_proc_desp_p = 'M' then
	
		select	x.vl_item
		into STRICT	vl_item_w
		from	tiss_conta_desp x
		where	x.nr_sequencia = nr_seq_item_fatur_p;
	
		result_w := tiss_ins_ret_denial_pck.get_item_paid_value(null, nr_seq_item_fatur_p);
	
	end if;


	return vl_item_w - result_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
