CREATE OR REPLACE PROCEDURE tws_notificacao_hpms_pck.atualizar_notificacao ( nr_seq_notificacao_p pls_notificacao_tws.nr_sequencia%type, ie_status_p pls_notificacao_tws.ie_status%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

if (nr_seq_notificacao_p IS NOT NULL AND nr_seq_notificacao_p::text <> '') then
	update 	pls_notificacao_tws
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario   		= nm_usuario_p,		
		ie_status		= ie_status_p
	where	nr_sequencia		= nr_seq_notificacao_p;
end if;

END;

$body$
