CREATE OR REPLACE PROCEDURE tws_notificacao_hpms_pck.tws_mensagem_gerar_leitura ( nr_seq_comunicado_p pls_comunic_externa_web .nr_sequencia%type, ie_tipo_acesso_p pls_comunic_ext_hist_web.ie_tipo_acesso%type, ds_login_p wsuite_usuario.ds_login%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


