BEGIN


if (nr_seq_comunicado_p IS NOT NULL AND nr_seq_comunicado_p::text <> '' AND ds_login_p IS NOT NULL AND ds_login_p::text <> '') then

	select 	max(cd_pessoa_fisica)
	into STRICT 	cd_pessoa_fisica_w
	from	wsuite_usuario
	where 	ds_login = ds_login_p;
	
	insert into pls_comunic_ext_hist_web(
		nr_sequencia, nr_seq_comunicado, dt_atualizacao,
		nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		nm_usuario_leitura, dt_leitura, ds_email,
		cd_pessoa_fisica_resp, ie_tipo_acesso,
		ds_login)
	values (nextval('pls_comunic_ext_hist_web_seq'), nr_seq_comunicado_p, clock_timestamp(), 
		nm_usuario_p, clock_timestamp(), nm_usuario_p,
		nm_usuario_p, clock_timestamp(), null,
		cd_pessoa_fisica_w, ie_tipo_acesso_p,
		ds_login_p);
	commit;	
end if;

END;	

