/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Utilizado para o processo de Justificativa de contas e de autorizacao
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
CREATE OR REPLACE PROCEDURE tws_notificacao_hpms_pck.tws_notif_justif_prestador ( nm_usuario_p usuario.nm_usuario%type, ie_tipo_processo_p pls_notificacao_tws.ie_tipo_processo%type, ie_status_p pls_notificacao_tws.ie_status%type, nr_seq_conta_p pls_conta.nr_sequencia%type) AS $body$
DECLARE


nr_seq_notificacao_tws_w	pls_notificacao_tws.nr_sequencia%type;
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;


BEGIN

select 	max(b.nr_seq_prestador)
into STRICT	nr_seq_prestador_w
from	pls_conta a,
	pls_protocolo_conta b
where	b.nr_sequencia	= a.nr_seq_protocolo
and	a.nr_sequencia	= nr_seq_conta_p;

if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then
	begin
		select	max(nr_sequencia)
		into STRICT	nr_seq_notificacao_tws_w
		from	pls_notificacao_tws
		where	nr_seq_prestador	= nr_seq_prestador_w
		and	ie_tipo_processo	= ie_tipo_processo_p
		and	nr_seq_conta		= nr_seq_conta_p;
	exception
	when others then
		nr_seq_notificacao_tws_w	:= null;
	end;
	
	if ( coalesce(nr_seq_notificacao_tws_w::text, '') = '' ) then
		CALL tws_notificacao_hpms_pck.tws_gerar_notificacao_web( ie_tipo_processo_p, null, null, null, nr_seq_conta_p, nr_seq_prestador_w, null, null, nm_usuario_p);
	else
		CALL tws_notificacao_hpms_pck.atualizar_notificacao( nr_seq_notificacao_tws_w, 'P', nm_usuario_p);
	end if;
	
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
