/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Utilizado portal do para confirmar recebimento da carteirinha
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
CREATE OR REPLACE PROCEDURE tws_solic_carteira_hpms_pck.tws_confirma_receb_carteira ( nr_seq_carteira_p pls_segurado_carteira.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_aplicacao_p wsuite_configuracao.ie_aplicacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_registros_w		bigint;



BEGIN

select 	count(1)
into STRICT	qt_registros_w
from 	pls_carteira_emissao
where 	nr_seq_seg_carteira = nr_seq_carteira_p;

if (qt_registros_w > 0) then

	update 	pls_carteira_emissao 
	set    	dt_entrega_beneficiario = clock_timestamp(), 
		nm_usuario_entrega 	= nm_usuario_p 
        where  	nr_seq_seg_carteira 	= nr_seq_carteira_p;
	
	/* Rotina utilizada para atualizar o estagio da emissao da carteirinha*/

	CALL pls_alterar_estagios_cartao(nr_seq_carteira_p, clock_timestamp(), 7, cd_estabelecimento_p, nm_usuario_p);

end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
