/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Utilizado portal do para solicitacao de segunda via de carteira
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
CREATE OR REPLACE PROCEDURE tws_solic_carteira_hpms_pck.tws_solic_via_carteira ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_carteira_p pls_segurado_carteira.nr_sequencia%type, nr_seq_motivo_via_p pls_motivo_via_adicional.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_aplicacao_p wsuite_configuracao.ie_aplicacao%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_exige_aprov_carteira_w	pls_param_beneficiario_tws.ie_exige_aprov_carteira%type;


BEGIN

if (ie_aplicacao_p = 2) then
	select	coalesce(max(ie_exige_aprov_carteira), 'N')
	into STRICT	ie_exige_aprov_carteira_w
	from	pls_param_beneficiario_tws
	where	cd_estabelecimento	= cd_estabelecimento_p;
	
elsif (ie_aplicacao_p = 6) then
	select	coalesce(max(ie_exige_aprov_carteira), 'N')
	into STRICT	ie_exige_aprov_carteira_w
	from	pls_param_estipulante_tws
	where	cd_estabelecimento	= cd_estabelecimento_p;
end if;


/*Regra criada na funcao OPS - Cadastro de Regras/OPS-Atendimento/Regra comunicacao interna*/

CALL pls_gerar_ci_solic_portal_web(5, null, null, nr_seq_segurado_p, null, 'N', nm_usuario_p);

if ( ie_exige_aprov_carteira_w = 'S') then
	insert into pls_solic_carteira_adic(	nr_sequencia, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_carteira,
						nr_seq_motivo_via, cd_funcao, ie_tipo_processo,
						ie_criar_lote_via_adic, nm_usuario_solic, dt_solicitacao,
						ie_status)
				values (	nextval('pls_solic_carteira_adic_seq'), clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, nr_seq_carteira_p, 
						nr_seq_motivo_via_p, 1248, 'W',
						'N', nm_usuario_p, clock_timestamp(),
						0);	
else
	CALL pls_gerar_via_carteira(nr_seq_carteira_p,nr_seq_motivo_via_p,nm_usuario_p,1248,'W','N');
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
