CREATE OR REPLACE PROCEDURE ugf_wheb_integr_sen_titpagar.cancelar_tit_pagar (cd_filial_p text, cd_fornecedor_p text, nr_titulo_p text, nr_seq_motivo_cancel_p text, dt_cancelamento_p timestamp, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
	nr_titulo_w		bigint;
	dt_cancelamento_w	timestamp;
	ds_erro_w		varchar(255);
	
	 
	
BEGIN 
	 
	dt_cancelamento_w	:= coalesce(dt_cancelamento_p,clock_timestamp());
	 
	select	coalesce(max(nr_titulo),0) 
	into STRICT	nr_titulo_w 
	from	titulo_pagar 
	where	nr_titulo_externo	= nr_titulo_p;
	 
	if (nr_titulo_w <> 0) then 
		begin 
		CALL cancelar_titulo_pagar(	nr_titulo_w, nm_usuario_p, dt_cancelamento_w);
		exception when others then 
			ds_erro_w	:= substr('Erro ao cancelar o titulo no Tasy: (' || nr_titulo_w || ') ' || sqlerrm(SQLSTATE),1,255);
		end;
		/*commit;*/
 
	end if;
	end;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
