SET client_encoding TO 'UTF8';

---------------------------------------------------------------
--- controlar o processo de valorizacao mensal do estoque
---------------------------------------------------------------
CREATE TYPE reg_saldos AS (
	cd_material		bigint,
	ie_status_valorizacao	varchar(01));


CREATE OR REPLACE PROCEDURE val_estoque_media_ponderada.val_prod ( dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


type saldos is table of reg_saldos index by integer;

dt_atualizacao_w			timestamp			:= clock_timestamp();
vl_estoque_w			double precision		:= 0;
qt_estoque_w			double precision		:= 0;
vl_custo_medio_w		double precision		:= 0;
cd_operacao_producao_w		integer		:= 0;
cd_operacao_baixa_w		integer		:= 0;
nr_movimento_estoque_w		bigint		:= 0;
i				integer			:= 0;
saldos_w				saldos;

c01 CURSOR FOR
SELECT distinct
	a.cd_material,
	ie_status_valorizacao
from	saldo_estoque a
where	a.cd_estabelecimento    = cd_estabelecimento_p
and	a.dt_mesano_referencia  = dt_mesano_referencia_p
order by	ie_status_valorizacao,
		cd_material;

Vet01	C01%RowType;


BEGIN

select	cd_oper_producao
into STRICT	cd_operacao_producao_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

/* marcus 18/04/2006 os pro */

delete  /*+index (a salescm_i1) */from	saldo_estoque_cm_diario
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p;

update	saldo_estoque a
set	ie_status_valorizacao   = 'I'
where	a.cd_estabelecimento    = cd_estabelecimento_p
and	a.dt_mesano_referencia  = dt_mesano_referencia_p;

update	saldo_estoque a
set	ie_status_valorizacao   = 'P'
where	a.cd_estabelecimento    = cd_estabelecimento_p
and	a.dt_mesano_referencia  = dt_mesano_referencia_p
and	a.cd_material in (SELECT	distinct(cd_material)
	from	movimento_estoque
	where	cd_estabelecimento		= cd_estabelecimento_p
	and	dt_mesano_referencia	= dt_mesano_referencia_p
	and	cd_operacao_estoque	= cd_operacao_producao_w);


open C01;
loop
fetch C01 into
	Vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	i				:= i + 1;
	saldos_w[i].cd_material		:= Vet01.cd_material;
	saldos_w[i].ie_status_valorizacao	:= Vet01.ie_status_valorizacao;
	end;
end loop;
close C01;



for i in 1..saldos_w.Count loop
	begin
	if (saldos_w[i].ie_status_valorizacao = 'P') then
		CALL val_estoque_media_ponderada.val_custo_producao(saldos_w[i].cd_material, cd_estabelecimento_p, dt_mesano_referencia_p, nm_usuario_p);
	end if;


	SELECT * FROM val_estoque_media_ponderada.val_valorizar_movto(saldos_w[i].cd_material, cd_estabelecimento_p, dt_mesano_referencia_p, nm_usuario_p, nr_movimento_estoque_w, qt_estoque_w, vl_estoque_w, vl_custo_medio_w) INTO STRICT nr_movimento_estoque_w, qt_estoque_w, vl_estoque_w, vl_custo_medio_w;
	CALL val_estoque_media_ponderada.val_saldo(saldos_w[i].cd_material, cd_estabelecimento_p, dt_mesano_referencia_p, qt_estoque_w, vl_estoque_w, vl_custo_medio_w, nm_usuario_p);
	CALL val_estoque_media_ponderada.val_dif_saldo(saldos_w[i].cd_material, cd_estabelecimento_p, dt_mesano_referencia_p, nr_movimento_estoque_w);
	end;
	commit;
end loop;
commit;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
