---------------------------------------------------------------
--- controlar o processo de valorizacao do consignado
---------------------------------------------------------------
CREATE OR REPLACE PROCEDURE val_mensal_consignado.val_consig_prod ( dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint , nm_usuario_p text) AS $body$
DECLARE


dt_atualizacao_w		timestamp			:= clock_timestamp();
vl_custo_medio_w		double precision		:= 0;
cd_material_w		bigint		:= 0;
nr_movimento_estoque_w	bigint		:= 0;
cd_fornecedor_w		varchar(14);


c01 CURSOR FOR
SELECT	distinct cd_material,
	cd_fornecedor
from	fornecedor_mat_consignado
where	cd_estabelecimento    = cd_estabelecimento_p
and	dt_mesano_referencia  = dt_mesano_referencia_p
order by cd_material;

BEGIN

open c01;
loop
fetch c01 into
	cd_material_w,
	cd_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	vl_custo_medio_w := val_mensal_consignado.val_consig_cpv(
		cd_material_w, cd_fornecedor_w, cd_estabelecimento_p, dt_mesano_referencia_p, nm_usuario_p, vl_custo_medio_w);

	CALL val_mensal_consignado.val_consig_saldo(
		cd_material_w,
		cd_fornecedor_w,
		cd_estabelecimento_p,
		dt_mesano_referencia_p,
		vl_custo_medio_w,
		nm_usuario_p);

	nr_movimento_estoque_w := val_mensal_consignado.val_consig_atualizar_cpv(
		cd_material_w, cd_fornecedor_w, cd_estabelecimento_p, dt_mesano_referencia_p, vl_custo_medio_w, nm_usuario_p, nr_movimento_estoque_w);

	CALL val_mensal_consignado.val_consig_dif_saldo(
		cd_material_w,
		cd_fornecedor_w,
		cd_estabelecimento_p,
		dt_mesano_referencia_p,
		nr_movimento_estoque_w);
	commit;
end loop;
close c01;

commit;

END;

$body$
