CREATE OR REPLACE PROCEDURE val_mensal_estoque.val_est_saldo ( cd_material_p bigint, cd_estabelecimento_p bigint , dt_mesano_referencia_p timestamp, vl_custo_medio_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


cd_local_estoque_w		integer;
ds_erro_w			varchar(255)   := '';


BEGIN
update	saldo_estoque
set	vl_estoque			= coalesce(coalesce(qt_estoque,0) * coalesce(vl_custo_medio_p,0),0),
	vl_custo_medio		= coalesce(vl_custo_medio_p,0),
	nm_usuario			= nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	ie_status_valorizacao	= 'C'
where	cd_estabelecimento		= cd_estabelecimento_p
and	cd_material			= cd_material_p
and	dt_mesano_referencia		= dt_mesano_referencia_p;

if (ie_commit_p = 'S') then
	commit;
end if;

delete	from custo_mensal_material
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_referencia		= dt_mesano_referencia_p
and	cd_material		= cd_material_p
and	ie_tipo_custo		= 'E';

begin
insert into custo_mensal_material(
	cd_estabelecimento,
	dt_referencia,
	cd_material,
	ie_tipo_custo,
	dt_atualizacao,
	nm_usuario,
	vl_custo)
values (cd_estabelecimento_p,
	dt_mesano_referencia_p,
	cd_material_p,
	'E',
	clock_timestamp(),
	nm_usuario_p,
	vl_custo_medio_p);
exception
    when others then
        ds_erro_w   := WHEB_MENSAGEM_PCK.get_texto(280121);
end;
END;

