CREATE OR REPLACE FUNCTION validar_integracao_metodo_pck.tratar_parametro (cd_funcao_p integer, parametro_p integer, cd_perfil_p text, nm_usuario_p text, cd_estabelecimento_p text) RETURNS varchar AS $body$
DECLARE

    retorno varchar(5000);
    cd_dominio_w integer;
    vl_parametro_w varchar(5000);
    qt_registro_w integer;
    ds_parametro_w varchar(5000) := '';

BEGIN
    vl_parametro_w := obter_valor_param_usuario(cd_funcao_p,parametro_p,cd_perfil_p,nm_usuario_p, cd_estabelecimento_p);
    select count(0) into STRICT qt_registro_w from funcao_parametro where nr_sequencia = parametro_p and cd_funcao = cd_funcao_p;
    if qt_registro_w > 0 then
      select cd_dominio into STRICT cd_dominio_w from funcao_parametro where nr_sequencia = parametro_p and cd_funcao = cd_funcao_p;

      select count(0) into STRICT qt_registro_w from valor_dominio where cd_dominio = cd_dominio_w and vl_dominio = vl_parametro_w;
      if qt_registro_w > 0 then
        select ds_valor_dominio into STRICT ds_parametro_w from valor_dominio where cd_dominio = cd_dominio_w and vl_dominio = vl_parametro_w;
      end if;
    end if;

    retorno := '(' || vl_parametro_w || ') ' || ds_parametro_w;
    return retorno;
  end;

