CREATE OR REPLACE FUNCTION validar_integracao_metodo_pck.validar_espaco (host_p text) RETURNS varchar AS $body$
DECLARE

    qt_registro integer;
    retorno varchar(5000) := '';

BEGIN
    if (host_p <> trim(both host_p)) then
      retorno := validar_integracao_metodo_pck.tratar_mensagem(retorno, 'Existe espaco em branco no final do host: "'|| host_p||'"');
    end if;
    qt_registro := position(' ' in host_p);
    if (qt_registro <> 0) then
      retorno := validar_integracao_metodo_pck.tratar_mensagem(retorno, 'Existe espaco em branco no host: "'|| host_p||'"');
    end if;
    return retorno;
  end;


$body$
