CREATE OR REPLACE FUNCTION validar_integracao_metodo_pck.validar_regra_acl (host_p text) RETURNS varchar AS $body$
DECLARE

    qt_registro_w bigint;
    retorno varchar(5000) := '';
    perm_DBA_NETWORK_ACLS_w bigint;

BEGIN
    OBTER_VALOR_DINAMICO('select count(1) from dual, DBA_NETWORK_ACLS', perm_DBA_NETWORK_ACLS_w);
    if (perm_DBA_NETWORK_ACLS_w = 0) then
      retorno := validar_integracao_metodo_pck.tratar_mensagem(retorno, 'Nao foi possivel validar regra acl: ' || chr(10) || ' - Sem permissao de leitura na tabela "DBA_NETWORK_ACLS"');
    else
      OBTER_VALOR_DINAMICO_4000_BV('select count(0) from DBA_NETWORK_ACLS where trim(host) = :host', 'host='||host_p, qt_registro_w);
    
      if (coalesce(qt_registro_w::text, '') = '') then
        retorno := 'Verificar: Host "'|| host_p || '" sem permissao ACL';
      end if;
    end if;
    return retorno;
  end;


$body$
