CREATE OR REPLACE PROCEDURE validar_updates_aut_conv_pck.validar_upd_mat_aut_convenio (cd_material_glo_p material_vinculo_global.cd_material_glo%TYPE, nr_sequencia_autor_p material_autorizado.nr_sequencia_autor%TYPE, ds_mensagem_retorno_p INOUT integration_call_log.ds_notes%TYPE, nr_sequencia_p material_autorizado.nr_sequencia%TYPE) AS $body$
DECLARE


    cd_material_glo_w material_vinculo_global.cd_material_glo%TYPE := cd_material_glo_p;
    nr_sequencia_autor_w material_autorizado.nr_sequencia_autor%TYPE := nr_sequencia_autor_p;
    nr_sequencia_w material_autorizado.nr_sequencia%TYPE := nr_sequencia_p;
    cd_categoria_w atend_categoria_convenio.cd_categoria%TYPE;
    cd_plano_convenio_w atend_categoria_convenio.cd_plano_convenio%TYPE;
    cd_convenio_w autorizacao_convenio.cd_convenio%TYPE;
    cd_setor_atendimento_w autorizacao_convenio.cd_setor_resp%TYPE;
    cd_estabelecimento_w autorizacao_convenio.cd_estabelecimento%TYPE;
    quantidade_registros_w integer;


BEGIN
        
        select count(1)
        into STRICT quantidade_registros_w
        from material_autorizado
        where nr_sequencia = nr_sequencia_w
        and nr_sequencia_autor = nr_sequencia_autor_w;

        IF (quantidade_registros_w = 0) THEN
            ds_mensagem_retorno_p := current_setting('validar_updates_aut_conv_pck.separador_linha_w')::varchar(50)CHR(13)||||'There are no records with the Transaction Id: '||nr_sequencia_autor_w|| ' and materialSequence '||
                                     nr_sequencia_w ||' . Validate the information and try again.';
            CALL validar_updates_aut_conv_pck.grava_log_processo_erro(ds_mensagem_retorno_p);
        END IF;

    EXCEPTION
        WHEN no_data_found THEN
            ds_mensagem_retorno_p := 'An error occurred validating the update of the insurance authorization materials, Transaction Id: '||nr_sequencia_autor_w
                                     || ' and materialSequence '|| nr_sequencia_w ||' and Material Code: '||cd_material_glo_w;
            CALL validar_updates_aut_conv_pck.grava_log_processo_erro(ds_mensagem_retorno_p||sqlerrm);
            CALL wheb_mensagem_pck.exibir_mensagem_abort('An error occurred while consulting the material records of the insurance authorization. Error: '|| sqlerrm);
    END;



$body$
