CREATE OR REPLACE PROCEDURE validar_updates_aut_conv_pck.validar_update_auto_convenio (nr_seq_autorizacao_p tiss_retorno_autorizacao.nr_seq_autorizacao%type, nr_seq_estagio_p autorizacao_convenio.nr_seq_estagio%type, ds_mensagem_retorno_p INOUT integration_call_log.ds_notes%TYPE) AS $body$
DECLARE


    nr_seq_autorizacao_w tiss_retorno_autorizacao.nr_seq_autorizacao%type := nr_seq_autorizacao_p;
    nr_seq_estagio_w autorizacao_convenio.nr_seq_estagio%type := nr_seq_estagio_p;
    quantidade_registros_w integer;


BEGIN
        select count(1)
        into STRICT quantidade_registros_w
        from autorizacao_convenio
        where nr_sequencia = nr_seq_autorizacao_w;

        if (quantidade_registros_w = 0) then
            ds_mensagem_retorno_p := current_setting('validar_updates_aut_conv_pck.separador_linha_w')::varchar(50)CHR(13)||||'There are no records with the Transaction Id '||nr_seq_autorizacao_w||
                                     ' . Validate the information and try again';
        end if;

        quantidade_registros_w := 0;

        select count(1)
        into STRICT quantidade_registros_w
        from estagio_autorizacao
        where nr_sequencia = nr_seq_estagio_w;

        if (quantidade_registros_w = 0) then
            ds_mensagem_retorno_p := ds_mensagem_retorno_p||current_setting('validar_updates_aut_conv_pck.separador_linha_w')::varchar(50)CHR(13)||||'There is no Authorization Stage registered with the authorization Situation '
                                     ||nr_seq_estagio_w||' . Validate the information and try again';
        else
            ds_mensagem_retorno_p := ds_mensagem_retorno_p||'';
        end if;

        if (ds_mensagem_retorno_p IS NOT NULL AND ds_mensagem_retorno_p::text <> '') then
            CALL validar_updates_aut_conv_pck.grava_log_processo_erro(ds_mensagem_retorno_p);
        end if;

    EXCEPTION
        WHEN no_data_found THEN
             CALL wheb_mensagem_pck.exibir_mensagem_abort('An error occurred while consulting the insurance authorization records. Error: '|| sqlerrm);
    END;


$body$
