CREATE OR REPLACE FUNCTION valores_adep_pck.obter_val_sol_agrupada (ie_campo_buscado_p text, ie_alteracao_p text, nr_seq_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE


    ds_result_w     varchar(50);
    v_sql_w         varchar(500);


BEGIN

    v_sql_w := 'select max('||ie_campo_buscado_p||')
                from prescr_solucao_evento
                where nr_sequencia = (  select max(nr_sequencia)
