                                        and ie_alteracao in  (select cd_registro from table(lista_pck.obter_lista_char(:c)))
                                        and ie_evento_valido = ''S'')';

    EXECUTE v_sql_w into STRICT ds_result_w  using nr_seq_cpoe_p, ie_alteracao_p;

    return ds_result_w;

    EXCEPTION
          WHEN OTHERS THEN
           return null;

END;

