SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION vent_bundle_report_pck.get_care_plan (nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


	qt_reg_w		bigint;
	ie_care_plan_w		varchar(1);
	dt_care_plan_w		timestamp;
	dt_care_plan_format_w	varchar(15);
	ds_retorno_w		varchar(15);

	
BEGIN

	select	count(1)
	into STRICT	qt_reg_w
	from	pe_prescricao a,
		pe_prescr_item_result b
	where	b.nr_seq_prescr = a.nr_sequencia
	and	((a.dt_inicio_prescr between(dt_inicial_p - 1) and (dt_final_p + 1))
		or (a.dt_validade_prescr between(dt_inicial_p - 1) and (dt_final_p + 1)))
	and	a.nr_atendimento = nr_atendimento_p
	and	a.ie_situacao = 'A'
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

	if (qt_reg_w > 0) then
		ie_care_plan_w := 'Y';
	else
		begin
		
		ie_care_plan_w := 'X';

		select	max(a.dt_validade_prescr)
		into STRICT	dt_care_plan_w
		from	pe_prescricao a,
			pe_prescr_item_result b
		where	b.nr_seq_prescr = a.nr_sequencia
		and	a.nr_atendimento = nr_atendimento_p
		and	a.ie_situacao = 'A'
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
		
		if (dt_care_plan_w IS NOT NULL AND dt_care_plan_w::text <> '') then
			dt_care_plan_format_w := to_char(dt_care_plan_w,'mm/dd hh24:mi');
		end if;
		
		end;
	end if;
	
	if (ie_opcao_p = 'IE') then
		ds_retorno_w := ie_care_plan_w;
	elsif (ie_opcao_p = 'DT') then
		ds_retorno_w := dt_care_plan_format_w;
	end if;

	return	ds_retorno_w;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
