SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION vent_bundle_report_pck.get_ie_hob (nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w	varchar(15) := 'NA';
	qt_reg_w	bigint;
	ie_opcao_w	varchar(3);
	dt_sinal_vital_w	timestamp := null;

	
BEGIN

	begin
		select	count(1)
		into STRICT	qt_reg_w
		from	atendimento_sinal_vital
		where	nr_atendimento = nr_atendimento_p
		and	qt_angulo_cabeceira > 30
		and	(trunc(dt_sinal_vital + (1 / 24) * 12) between trunc(dt_inicial_p) and trunc(dt_final_p)
			or    trunc(dt_sinal_vital - (1 / 24) * 12) between trunc(dt_inicial_p) and trunc(dt_final_p));
		
	exception
	when no_data_found then
		qt_reg_w := 0;
	end;
	
	if (qt_reg_w > 0) then

		ie_opcao_w := 'Y';

		select	max(dt_sinal_vital)
		into STRICT	dt_sinal_vital_w
		from	atendimento_sinal_vital
		where	nr_atendimento = nr_atendimento_p
		and	qt_angulo_cabeceira > 30
		and	(trunc(dt_sinal_vital + (1 / 24) * 12) between trunc(dt_inicial_p) and trunc(dt_final_p)
			or    trunc(dt_sinal_vital - (1 / 24) * 12) between trunc(dt_inicial_p) and trunc(dt_final_p));	

	else
		ie_opcao_w := 'NA';
	end if;	

	if (ie_opcao_p = 'IE') then
		ds_retorno_w := ie_opcao_w;
	elsif (ie_opcao_p = 'DT') then	
		ds_retorno_w := to_char(dt_sinal_vital_w,'mm/dd hh:mi');
	end if;
	
	return	ds_retorno_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
