CREATE OR REPLACE PROCEDURE vipe_gerar_horarios_pck.vipe_obter_dialise ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, nr_seq_dialise_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupar_dose_esp_p text, ie_diluicao_acm_sn_gestao_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


	nr_seq_wadep_w		bigint;
	nr_prescricao_w		bigint;
	nr_seq_solucao_w	integer;
	ds_solucao_w		varchar(255);
	ie_acm_sn_w		varchar(1);
	ds_prescricao_w		varchar(240);
	ds_componentes_w	varchar(240);
	ie_status_solucao_w	varchar(15);
	ie_suspensa_w		varchar(1);
	dt_prev_prox_etapa_w	timestamp;
	ie_lib_pend_rep_w	varchar(1);
	cd_unid_med_qtde_w	varchar(30);
	ie_via_administracao_w	varchar(5);
	qt_solucao_total_w	double precision;
	ds_intervalo_w		varchar(240);
	nr_prescr_sol_w		varchar(2000);
	dt_fim_w		timestamp;
	dt_iniciosol_w		timestamp;
	ie_permite_acao_w	varchar(1);
	ie_modificada_w		varchar(1);

	c01 CURSOR FOR
	SELECT	nr_prescricao,
		nr_seq_solucao,
		ds_solucao,
		ie_acm_sn,
		ds_prescricao,
		ds_componentes,
		ie_status_solucao,
		null,
		dt_prev_prox_etapa,
		ie_tipo_dosagem,
		null ie_via_aplicacao,
		qt_solucao_total,
		ds_intervalo,
		ie_permite_acao,
		ie_modificada
	from	(
		SELECT	a.nr_prescricao,
			x.nr_seq_solucao,
			substr(upper(obter_valor_dominio(1934, c.IE_TIPO_HEMODIALISE)) || ' - ' ||  coalesce(substr(obter_descricao_padrao('PROTOCOLO_NPT', 'DS_NPT', x.nr_seq_protocolo),1,100),obter_prim_comp_sol(a.nr_prescricao,x.nr_seq_solucao)),1,240) ds_solucao,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
			substr(obter_desc_vol_etapa_adep2(a.nr_prescricao,x.nr_seq_solucao,x.ie_acm,x.ie_se_necessario),1,240) ds_prescricao,
			substr(obter_componentes_solucao(a.nr_prescricao,x.nr_seq_solucao,'S',nm_usuario_p,cd_estabelecimento_p),1,240) ds_componentes,
			substr(obter_status_solucao_prescr(1,a.nr_prescricao,x.nr_seq_solucao),1,3) ie_status_solucao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			x.dt_prev_prox_etapa,
			x.ie_tipo_dosagem,
			x.ie_via_aplicacao,
			x.qt_solucao_total,
			substr(obter_interv_solucao_vipe(x.ie_acm,x.ie_se_necessario,x.ie_urgencia,x.nr_etapas,x.qt_hora_fase) || CASE WHEN coalesce(x.ie_classif_agora::text, '') = '' THEN ''  ELSE ' - ' END  || obter_desc_intervalo(x.ie_classif_agora),1,240) ds_intervalo,
			obter_se_habilita_opcao('MD',cd_perfil_p) ie_permite_acao,
			x.ie_modificada
		from	hd_prescricao c,
			prescr_solucao x,
			prescr_medica a
		where	x.nr_prescricao = a.nr_prescricao
		and	x.nr_seq_dialise = c.nr_sequencia
		and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
		and	a.nr_atendimento = nr_atendimento_p
		and	a.dt_inicio_prescr between dt_iniciosol_w and dt_fim_w
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S'
		and	(x.nr_seq_dialise IS NOT NULL AND x.nr_seq_dialise::text <> '')
		and	((coalesce(x.ie_acm,'N') = 'S') or (exists (select 1 from prescr_mat_hor c where c.ie_agrupador = 13 and c.nr_prescricao = x.nr_prescricao and c.nr_seq_solucao = x.nr_seq_solucao and c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)))
		and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
		and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
		and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
		and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
		and	((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL',
																			cd_estabelecimento_p,
																			cd_setor_usuario_p,
																			cd_perfil_p,
																			null,
																			null,
																			null,
																			null,
																			a.cd_setor_atendimento,
																			null,
																			null, -- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e n√£o haviam passado nada
																			null) = 'S'))) -- nr_seq_exame_p
		and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
		and	((x.ie_status in ('I','INT')) or
			 ((obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) = 'N') and (x.dt_status between dt_inicial_horarios_p and dt_final_horarios_p)) or
			 ((get_ie_considera_horario = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')) or
			 ((obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
		group by
			a.nr_prescricao,
			x.nr_seq_solucao,
			substr(upper(obter_valor_dominio(1934, c.IE_TIPO_HEMODIALISE)) || ' - ' ||  coalesce(substr(obter_descricao_padrao('PROTOCOLO_NPT', 'DS_NPT', x.nr_seq_protocolo),1,100),obter_prim_comp_sol(a.nr_prescricao,x.nr_seq_solucao)),1,240),
			x.ie_acm,
			x.ie_se_necessario,
			nm_usuario_p,
			cd_estabelecimento_p,
			obter_desc_intervalo(x.ie_classif_agora),
			x.ie_suspenso,
			x.dt_prev_prox_etapa,
			x.ie_tipo_dosagem,
			x.ie_via_aplicacao,
			x.qt_solucao_total,
			CASE WHEN coalesce(x.ie_classif_agora::text, '') = '' THEN ''  ELSE ' - ' END ,
			x.ie_urgencia,
			x.nr_etapas,
			x.qt_hora_fase,
			x.ie_modificada
		) alias75
	group by
		nr_prescricao,
		nr_seq_solucao,
		ds_solucao,
		ie_acm_sn,
		ds_prescricao,
		ds_componentes,
		ie_status_solucao,
		null,
		dt_prev_prox_etapa,
		ie_tipo_dosagem,
		qt_solucao_total,
		ds_intervalo,
		ie_permite_acao,
		ie_modificada;

	
BEGIN

	SELECT * FROM vipe_gerar_horarios_pck.getdtrefsol(dt_validade_limite_p, dt_iniciosol_w, dt_fim_w) INTO STRICT dt_iniciosol_w, dt_fim_w;

	open c01;
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_solucao_w,
			ds_solucao_w,
			ie_acm_sn_w,
			ds_prescricao_w,
			ds_componentes_w,
			ie_status_solucao_w,
			ie_suspensa_w,
			dt_prev_prox_etapa_w,
			cd_unid_med_qtde_w,
			ie_via_administracao_w,
			qt_solucao_total_w,
			ds_intervalo_w,
			ie_permite_acao_w,
			ie_modificada_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin




		select	nextval('w_vipe_t_seq')
			into STRICT	nr_seq_wadep_w
			;

			insert into w_vipe_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,
				ds_item,
				ie_acm_sn,
				ds_prescricao,
				ds_diluicao,
				ie_status_item,
				ie_suspenso,
				dt_prev_term,
				nr_seq_proc_interno,
				nr_agrupamento,
				ie_diferenciado,
				nr_prescricoes,
				cd_item,
				cd_unid_med_qtde,
				ie_via_aplicacao,
				qt_item,
				ds_interv_prescr,
				ie_permite_acao,
				ie_modificada)
				values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'MD',
				nr_prescricao_w,
				nr_seq_solucao_w,
				SUBSTR(ds_solucao_w, 1, 240),
				ie_acm_sn_w,
				ds_prescricao_w,
				ds_componentes_w,
				ie_status_solucao_w,
				ie_suspensa_w,
				dt_prev_prox_etapa_w,
				0,
				0,
				'N',
				nr_prescricao_w,
				0,
				cd_unid_med_qtde_w,
				ie_via_administracao_w,
				qt_solucao_total_w,
				ds_intervalo_w,
				ie_permite_acao_w,
				ie_modificada_w);
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_gerar_horarios_pck.vipe_obter_dialise ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, nr_seq_dialise_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupar_dose_esp_p text, ie_diluicao_acm_sn_gestao_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
