CREATE OR REPLACE PROCEDURE vipe_gerar_horarios_pck.vipe_obter_dieta_oral_servico ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_horarios_dietas_orais_p text, ie_exibe_sem_lib_farm_p text, ie_exibe_dietas_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


	nr_prescricao_w		bigint;
	cd_refeicao_w		varchar(15);
	ds_refeicao_w		varchar(255);
	ie_lib_pend_rep_w	varchar(1);
	nr_seq_wadep_w		bigint;
	ie_status_item_w	varchar(1);
	ds_inter_prescr_w	varchar(15);
	cd_intervalo_w		varchar(7);
	dt_fim_w		timestamp;


	C01 CURSOR FOR
	SELECT 	to_char(x.nr_sequencia),
		b.ds_servico,
		coalesce(d.ie_suspenso,'N')
	from	prescr_dieta d,
		nut_servico b,
		prescr_medica a,
		nut_atend_serv_dia x
	where	d.nr_prescricao	= a.nr_prescricao
	and 	x.nr_atendimento = a.nr_atendimento
	and	x.nr_seq_servico = b.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	Obter_se_servico_vig_dieta(a.dt_inicio_prescr, a.dt_validade_prescr, a.nr_Atendimento, '1', ie_exibir_hor_realizados_p, ie_exibir_hor_suspensos_p) = 'S'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	x.dt_servico between dt_inicial_horarios_p and dt_final_horarios_p
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and 	((coalesce(b.IE_TIPO_PRESCRICAO_SERVICO::text, '') = '') or (b.IE_TIPO_PRESCRICAO_SERVICO = '1'))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(x.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	x.ie_status_adep = 'Y'
	group by to_char(x.nr_sequencia),
			b.ds_servico,
			d.ie_suspenso
	
union

	SELECT	to_char(x.nr_sequencia),
		b.ds_servico,
		coalesce(d.ie_suspenso,'N')
	from	prescr_dieta d,
		nut_servico b,
		nut_atend_serv_dia_rep c,
		prescr_medica a,
		nut_atend_serv_dia x
	where	d.nr_prescricao	= a.nr_prescricao
	and 	x.nr_atendimento = a.nr_atendimento
	and	x.nr_seq_servico = b.nr_sequencia
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.nr_prescricao = c.nr_prescr_oral
	and	c.nr_seq_serv_dia = x.nr_sequencia
	and	Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S'
	and	x.dt_servico between dt_inicial_horarios_p and dt_final_horarios_p
	and	Obter_se_servico_vig_dieta(a.dt_inicio_prescr, a.dt_validade_prescr, a.nr_Atendimento, '1', ie_exibir_hor_realizados_p, ie_exibir_hor_suspensos_p) = 'S'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S') or
		((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = ''))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(x.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and 	((coalesce(b.IE_TIPO_PRESCRICAO_SERVICO::text, '') = '') or (b.IE_TIPO_PRESCRICAO_SERVICO = '1'))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	x.ie_status_adep <> 'Y'
	group by to_char(x.nr_sequencia),
			b.ds_servico,
			d.ie_suspenso
	order by 2;

	
BEGIN
	dt_fim_w	:= vipe_gerar_horarios_pck.getdtfimvalidade(dt_validade_limite_p);

	open c01;
	loop
	fetch c01 into	cd_refeicao_w,
			ds_refeicao_w,
			ie_status_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	nextval('w_vipe_t_seq')
		into STRICT	nr_seq_wadep_w
		;
		insert into w_vipe_t(
			nr_sequencia,
			nm_usuario,
			ie_tipo_item,
			cd_item,
			ds_item,
			ie_acm_sn,
			ie_diferenciado,
			nr_seq_proc_interno,
			nr_agrupamento,
			ie_status_item
			)
		values (
			nr_seq_wadep_w,
			nm_usuario_p,
			'DE',
			cd_refeicao_w,
			SUBSTR(ds_refeicao_w, 1, 240),
			'N',
			'N',
			0,
			0,
			ie_status_item_w
			);

		end;
	end loop;
	close c01;
	END;

$body$
