			'Dose especial' ds_intervalo,
			coalesce(x.qt_total_dias_lib,0) qt_total_dias_lib,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'P') ie_composto,
