					nr_seq_wadep_w,
					nm_usuario_p,
					'NAN',
					nr_prescricao_w,
					nr_seq_npt_w,
					nr_seq_npt_w,
					SUBSTR(ds_npt_w, 1, 240),
					'N',
					'N',
					0,
					0,
					ie_status_solucao_w,
					current_setting('vipe_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
					ds_intervalo_w,
					qt_volume_diario_w,
					'ml',
					current_setting('vipe_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000),
					ie_forma_w,
					nr_seq_prot_w,
					ie_permite_acao_w);

				PERFORM set_config('vipe_gerar_horarios_pck.nr_prescricoes_w', '', false);
				PERFORM set_config('vipe_gerar_horarios_pck.nr_prescricoes_w', adep_juntar_prescricao(current_setting('vipe_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000),nr_prescricao_w), false);

			end if;

			PERFORM set_config('vipe_gerar_horarios_pck.nr_prescricoes_w', adep_juntar_prescricao(current_setting('vipe_gerar_horarios_pck.nr_prescricoes_w')::varchar(4000),nr_prescricao_w), false);

			end;
		end loop;
		close c02;
		end;
	end if;
	END;

$body$
