CREATE OR REPLACE PROCEDURE vipe_gerar_horarios_pck.vipe_obter_rec ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_lib_pend_rep_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE


	nr_seq_wadep_w		bigint;
	nr_prescricao_w		bigint;
	nr_seq_recomendacao_w	integer;
	cd_recomendacao_w	varchar(255);
	ds_recomendacao_w	varchar(4000);
	ie_acm_sn_w		varchar(1);
	cd_intervalo_w		varchar(50);
	qt_recomendacao_w	double precision;
	ds_intervalo_w		varchar(100);
	ie_status_w		varchar(1);
	current_setting('vipe_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15)	varchar(1);
	ds_interv_prescr_w	varchar(50);
	ds_rec_w		varchar(4000);
	dt_fim_w		timestamp;
	ie_permite_acao_w	varchar(1);

	c01 CURSOR FOR
	SELECT	CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END ,
		CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_recomendacao  ELSE null END   ELSE null END ,
		cd_recomendacao,
		ds_recomendacao,
		ie_acm_sn,
		cd_intervalo,
		qt_recomendacao,
		ds_prescricao,
		--decode(ie_acm_sn, 'S', decode(obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p), 'N', decode(ie_suspenso, 'S', ie_suspenso, null), null), null) ie_status,
		ie_suspenso ie_status,
		ds_intervalo,
		substr(ds_rec,1,2000),
		ie_permite_acao
	from	(
		SELECT	a.nr_prescricao,
			c.nr_seq_recomendacao,
			substr(coalesce(to_char(x.cd_recomendacao),x.ds_recomendacao),1,255) cd_recomendacao,
			coalesce(y.ds_tipo_recomendacao,x.ds_recomendacao) ds_recomendacao,
			obter_se_acm_sn(x.ie_acm, x.ie_se_necessario) ie_acm_sn,
			x.cd_intervalo,
			x.qt_recomendacao,
			w.ds_prescricao ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			substr(obter_desc_intervalo_prescr(x.cd_intervalo),1,15) ds_intervalo,
			x.ds_recomendacao ds_rec,
			obter_se_habilita_opcao('R',cd_perfil_p) ie_permite_acao
		FROM prescr_rec_hor c, prescr_medica a, prescr_recomendacao x
LEFT OUTER JOIN intervalo_prescricao w ON (x.cd_intervalo = w.cd_intervalo)
LEFT OUTER JOIN tipo_recomendacao y ON (x.cd_recomendacao = y.cd_tipo_recomendacao)
WHERE x.nr_prescricao = c.nr_prescricao and x.nr_sequencia = c.nr_seq_recomendacao and x.nr_prescricao = a.nr_prescricao and c.nr_prescricao = a.nr_prescricao and obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' and a.nr_atendimento = nr_atendimento_p and Obter_se_setor_vipe(a.cd_setor_atendimento) = 'S' and a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w and ((ie_data_lib_prescr_p = 'F') or (obter_se_prescr_lib_vipe(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p,c.dt_lib_horario) = 'S')) and ((ie_exibir_suspensos_p = 'S') or (coalesce(a.dt_suspensao::text, '') = '')) and coalesce(c.ie_situacao,'A') = 'A' and (((obter_se_acm_sn(x.ie_acm, x.ie_se_necessario) = 'N') and (c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)) or
			 ((get_ie_considera_horario = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')) or
			 ((obter_se_acm_sn(x.ie_acm, x.ie_se_necessario) = 'S') and (obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'))) and ((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = '')) and ((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = '')) and ((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'REC',
																			cd_estabelecimento_p,
