			substr(adep_obter_lib_pend_rep_gestao(a.dt_liberacao_medico,a.dt_liberacao,a.dt_liberacao_farmacia),1,1) ie_lib_pend_rep,
			substr(obter_desc_intervalo_prescr(x.cd_intervalo),1,15) ds_intervalo,
